sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/ ) {

var dao = new Cisco_DAOScript();//softwaretrackid

	// Assets
	var resultdevice = dao.createReferences(target.deviceid, "device_id", "softwaretrackid", target.softwaretrackid, "x_caci_cisco_bcs_cisco_devices", true);

// 	if(resultAssets){
// 		target.asset = resultAssets + "";
// 		target.update();
//	}
//Device Name
	var Devicename = dao.getDevicename(target.deviceid, "device_id", "x_caci_cisco_bcs_cisco_devices");
	target.name=Devicename;
	target.update();

	//Product Id
	var ProductId = dao.getProductdetails(target.deviceid, "device_id", "x_caci_cisco_bcs_cisco_devices");
	target.product_id=ProductId;
	gs.info("Production"+ProductId);
	target.update();

	//Software Version
	var SoftwareVersion = dao.getSoftwareVersion(target.deviceid, "device_id", "x_caci_cisco_bcs_cisco_devices");
	target.sw_version=SoftwareVersion;
	target.update();


})(source, map, log, target);
